package test;

public class StringExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String fname = "Shubham";
		String lname = "Varfa";
		String c = fname +" "+ lname;
		System.out.println("Fullname is: "+ c);
		System.out.println("Length is "+ c.length());
		System.out.println("To uppercase "+ c.toUpperCase());
		System.out.println("replace space with ,"+ c.replace(" ", ","));

	}

}
